nice.paraboloid = function(x,y)
{
    return(x^2+0.5*y^2)
}
x = y = seq(from = -4, to = 4, by = 0.2)
z = outer(x, y, nice.paraboloid)

persp(x, y, z,
      main="Plot of a Non-degenerate 2D Paraboloid",
      theta = 30, phi = 15,
      col = "springgreen", shade = 0.5)

rm(list = ls()) bumpy.function = function(x,y) { return(sin(x)+sin(y)) }
nice.paraboloid = function(x,y) { return(0.15*(x^2 + y^2)) #lambda = 0.15 is used }
x = y = seq(from = -4, to = 4, by = 0.2) bumpy = outer(x, y, bumpy.function) paraboloid = outer(x, y, nice.paraboloid) bumpy.plus.paraboloid = bumpy + paraboloid
persp(x, y, bumpy, main="Graph of a bumpy function with multiple minima", theta = 30, phi = 15, col = "springgreen", shade = 0.5)
